import React from 'react';
import { render } from "react-dom";
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import teal from '@material-ui/core/colors/teal'; //verde
import pink from '@material-ui/core/colors/pink'; //rosa
import cyan from '@material-ui/core/colors/cyan'; //azul
import grey from '@material-ui/core/colors/grey'; //azul
import yellow from '@material-ui/core/colors/yellow'; //yellow
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/styles';
import Logo from "../../Images/logo.png";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = theme => ({
  '@global': {
    body: {
      backgroundColor: theme.palette.common.white,
      primary: teal['A400'], // #F44336
      accent:pink[400] // #E040FB
    },
  },
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
});


class SignIn extends React.Component {


  render() {
    const { classes } = this.props;
    return (
      
      <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div>
        <Avatar >
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <form onSubmit={this.onSubmit} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="userName"
            autoComplete="email"
            autoFocus
            onChange={this.onUserNameChange}
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="passwordUser"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            onChange={this.onUserPassChange}
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            
          >
            Sign In
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <Link href="#" variant="body2">
                {"Don't have an account? Sign Up"}
              </Link>
            </Grid>
          </Grid>
        </form>

      </div>
      <Box mt={8}>
        <Copyright />
      </Box>
    </Container>
    );
  }

  onUserNameChange = e => {
    this.setState({ userName: e.target.value });
  };
  onUserPassChange = e => {
    this.setState({ passwordUser: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();
    let data = {
      mainEmail: this.state.userName,
      passwordUser: this.state.passwordUser
    }
    fetch("http://" + document.domain+":3500/logIn",{
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ mainEmail: this.state.userName, passwordUser: this.state.passwordUser })
    }).then(function (response) {
        return response.json(); // call the json method on the response to get JSON
    })
    .then(function (json) {
        if(json.Status =="S")
        console.log(json.Status);
    })
  };
}
export default SignIn;
